import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import { deleteTaskApi } from '../routerApi/taskApi';

const Task = () => {
  const location = useLocation()
  console.log("location", location.state)
  const [token, setToken] = useState(location.state)
  const navigate = useNavigate()
  const [taskData, setTaskData] = useState([])
  //get data
  const getAllData = async () => {
    try {
      if (token === null) {
        navigate("/", { state: null })

      } else {
        const res = await axios.get("http://localhost:5000/myData", { headers: { "N-Token": token } })
        console.log("res", res.data)
        setTaskData(res.data)
      }

    } catch (e) {
      console.log(e)
    }

  }
  useEffect(() => {
    getAllData()
  }, [token])

  const onCreateTask = () => {
    navigate("/addTask", { state: token })
  }
  const onEditTask = (val) => {
    let action = "update"
    navigate("/addTask", { state: [val, action, token] })
  }


  const onDeleteTask = async (id) => {
    try {
      const res = await deleteTaskApi(id)
      console.log(res.data)
      if (res.data.message === "task is deleted") {
        getAllData()
      }
    } catch (e) {
      console.log(e)
    }
  }
  return (
    <div>
      
      <nav>
        <button type="button" class="btn btn-success m-3 text-white" onClick={onCreateTask}>Create</button>
        <button type="button" class="btn btn-primary text-white" onClick={() => setToken(null)}>LogOut</button>
        <h1 className='Tit'>To<span style={{color:"green"}}>Do</span> List</h1>
      </nav>
      <div className='mainCard'>
      {
        taskData.map((val) => {
          return (
            
            <div class="card w-20 mb-3 m-4">
             <div class="card-body">
                <h5 class="card-title text-danger">{val.title}</h5>
                <p class="card-text">{val.description}</p>
                <button style={{ margin: "5px" }} onClick={() => onEditTask(val)}>Edit</button>
                <button style={{ margin: "5px" }} onClick={() => onDeleteTask(val._id)}>Delete</button>
                </div>
            </div>

          )
        })
      }
</div>
    </div>
  )
}

export default Task
